mapper.insert:
INSERT INTO test (
	test_title
	, test_date
) VALUES (
	#{testTitle}
	,#{testDate}
)

mapper.select:
SELECT test_no testNo , test_title testTitle , test_date testDate FROM test
test_title like CONCAT('%',#{searchWord},'%')
ORDER BY test_no DESC LIMIT #{beginRow}, #{rowPerPage}

mapper.insert:
INSERT INTO question (
	test_no
	, question_idx
	, question_title
) VALUES (
	#{testNo}
	,#{questionIdx}
	,#{questionTitle}
)

mapper.select:
SELECT question_no questionNo , test_no testNo , question_idx questionIdx , question_title questionTitle FROM question
test_no = #{testNo}
AND question_title like CONCAT('%',#{searchWord},'%')
ORDER BY question_idx DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) count FROM question
test_no = #{testNo}
AND question_title like CONCAT('%',#{searchWord},'%')

mapper.insert:
INSERT INTO example (
	question_no
	, example_idx
	, example_title
	, example_ox
) VALUES (
	#{questionNo}
	,#{exampleIdx}
	,#{exampleTitle}
	,#{exampleOx}
)

mapper.select:
SELECT example_no exampleNo , question_no questionNo , example_idx exampleIdx , example_title exampleTitle , example_ox exampleOx FROM example
question_no = #{questionNo}
AND example_title like CONCAT('%',#{searchWord},'%')
ORDER BY example_idx DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) count FROM example
question_no = #{questionNo}
AND example_title like CONCAT('%',#{searchWord},'%')
